import React, { Component } from "react";
// import Nav from "./components/Nav/index";
// import { Container, Row, Col } from "./components/Grid";
import Search from "./components/Search";
import Results from './components/Results';
import axios from "axios"
import API from "../utils/API";
// import Drink from './components/Drink';

class App extends Component {
  state = {
    search: "",
    drinkArray: []
  }

  handleInputChange = event => {
    this.setState({ search: event.target.value })
  }


  searchDatabase = (query = this.state.search) => {
    // event.preventDefault();
    API.getdrinks(this.state.search)
      .then((res) => {
        console.log(res);
        this.setState({
          drinkArray: res.data.drinks
        })
      })
  }

  render() {
    return (
      <div>
        <h1 className="text-center"><b>SpeakEasy</b></h1>
        <Search searchDatabase={this.searchDatabase} handleInputChange={this.handleInputChange} />

        <Results drinks={this.state.drinkArray} />

      </div >
    );
  }
}


export default App;
