import React from "react"
// import Drink from '../Drink';
import "./style.css"
// import Search from "../Search";
// import axios from 'axios';

function Results(props) {
  console.log(props.drinks)
  return (props.drinks.length === 0) ? (
    <div>
      <h4>sorry we couldn't find anything</h4>
    </div>
  ) : (
      <div className="row">
        {props.drinks.map((drink, index) => {
          return (
            <div className="col-md-3 p-3" id={drink.id} key={index}>
              <h4 className="text-center m-3">{drink.title}</h4>
              <div className="d-flex justify-content-center">
                <img src={drink.img} alt={drink.title} />
              </div>
            </div>
          )
        })}


        {/* <input id="search"></input>
        <button onClick={() => this.searchDatabase(document.getElementById("search").value)}>button</button> */}
        {/* <div className="drinks">
        <this.drinkRenderer />
      </div> */}
      </div>
    )

}
export default Results
