import React from "react"
// import Drink from '../Drink';
import "./style.css"
// import Search from "../Search";
// import axios from 'axios';

function Results(props) {

  console.log(props.drinks)
  return (props.drinks.length === 0) ? (
    <div>
      <h4 className="text-center">Search for a Drink</h4>
    </div>
  ) : (props.searchBy === "searchByName" || "searchByIngredient") ? (
    <div className="row">
      {props.drinks.map((drink, index) => {
        return (
          <div className="col-lg-4 col-md-6 col-sm-12 p-3" id={drink.id} key={index}>
            <h4 className="text-center">{drink.title}</h4>
            <div className="d-flex justify-content-center">
              <img src={drink.img} alt={drink.title} />
            </div>
          </div>
        )
      })}
    </div>
  ) : (props.searchBy === "searchIngredientByName") ? (
    <div className="row">
      {props.drinks.map((drink, index) => {
        return (
          <div className="col-lg-12 col-md-12 col-sm-12 p-3" id={drink.id} key={index}>
            <h4 className="text-center">{drink.title}</h4>
            <p>{drink.description}</p>
          </div>
        )
      })}
    </div>
  ) : (
          <div className="row">
            {props.drinks.map((drink, index) => {
              return (
                <div className="col-lg-12 col-md-12 col-sm-12 p-3" id={drink.id} key={index}>
                  <h4 className="text-center">{drink.title}</h4>
                  <p>{drink.description}</p>
                </div>
