import React, { Component } from "react";
// import Nav from "./components/Nav/index";
// import { Container, Row, Col } from "./components/Grid";
import Search from "./components/Search";
import Results from './components/Results';
import axios from "axios"
// import Drink from './components/Drink';

class App extends Component {
  state = {
    drinkArray: []
  }


  searchDatabase = (query = "margarita", qType = "s") => {
    axios.get(`https://www.thecocktaildb.com/api/json/v2/8673533/search.php?${qType}=${query}`)
      .then((res) => {
        console.log(res);
        this.setState({
          drinkArray: res.data.drinks
        })
      })
  }

  render() {
    return (
      <div>
        <h1 className="text-center"><b>SpeakEasy</b></h1>
        <Search searchDatabase={this.searchDatabase} />

        <Results drinks={this.state.drinkArray} />

      </div >
    );
  }
}


export default App;
