import React, { Component } from "react";
import Nav from "./components/Nav/index";
import { Container, Row, Col } from "./components/Grid";
import Search from "./components/Search";
import Results from './components/Results';
import axios from "axios"
import Drink from './components/Drink';

class App extends Component {
  state = {
    drinkArray: []
  }

  searchDatabase = (query = "margarita", qType = "s") => {
    axios.get(`https://www.thecocktaildb.com/api/json/v2/8673533/search.php?${qType}=${query}`)
      .then((res) => {
        console.log(res);
        this.setState({
          drinkArray: res.data.drinks
        })
      })
  }
  render() {

    return (
      <div>
        <h1 className="text-center"><b>SpeakEasy</b></h1>
        {/* <Search /> */}
        <Container>
          <div className="backgroundRow p-5 m-5">
            <div className="container my-4">
              <div className="row">
                <div className="col-md-2">
                </div>
                <div className="col-md-8">
                  <form>
                    <input id="search" class="form-control" type="text" placeholder="Search"></input>
                    <button type="submit" class="btn btn-secondary btn-md btn-block" onClick={() =>
                      Results.searchDatabase(document.getElementById("search").value)}>
                      Search</button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </Container>

        <Container>

        </Container>

      </div>
    );
  }
}

export default App;
