import React from 'react';
import Nav from "./components/Nav/index";
import { Container, Row, Col } from "./components/Grid";
import Search from "./components/Search";
import Results from './components/Results';
import axios from "axios"
import Drink from './components/Drink';

function App() {
  state = { drinkArray: [] }

  searchDatabase = (query = "margarita", qType = "s") => {
    axios.get(`https://www.thecocktaildb.com/api/json/v2/8673533/search.php?${qType}=${query}`)
      .then((res) => {
        console.log(res);
        this.setState({
          drinkArray: res.data.drinks
        })
      })
  }

  drinkRenderer = () => {

    if (this.state.drinkArray == null) {
      return (
        <div>
          <h4>sorry we couldn't find anything</h4>
        </div>
      )
    } else {
      let arr = this.state.drinkArray.map(
        drink => <Drink title={drink.strDrink} img={drink.strDrinkThumb} />)
      return (
        <div className="row">
          {arr}
        </div>
      )
    }
  }
  return (
    <div>
      <h1 className="text-center"><b>SpeakEasy</b></h1>
      {/* <Search /> */}
      <Container>
        <div className="backgroundRow p-5 m-5">
          <div className="container my-4">
            <div className="row">
              <div className="col-md-2">
              </div>
              <div className="col-md-8">
                <form>
                  <input id="search" class="form-control" type="text" placeholder="Search"></input>
                  <button type="submit" class="btn btn-secondary btn-md btn-block" onClick={() =>
                    this.searchDatabase(document.getElementById("search").value)}>
                    Search</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </Container>

      <Container>
        <div className="">
          {/* <input id="search"></input>
        <button onClick={() => this.searchDatabase(document.getElementById("search").value)}>button</button> */}
          <div className="drinks">
            <this.drinkRenderer />
          </div>
        </div>
      </Container>



    </div>
  );
}

export default App;
