import React from "react";
import "./style.css";
// import Results from "../Results";
// import axios from "axios";
// import drinks from "../Drink"


function Search(props) {
  return (
    <div className="backgroundRow p-5 m-5">
      <div className="container my-4">
        <div className="row">
          <div className="col-md-2">
          </div>
          <div className="col-md-8">
            {/* <form> */}
            <div class="input-group mb-3">
              <div class="input-group-prepend">
                <button class="btn btn-outline-secondary dropdown-toggle" type="button" data-toggle="dropdown"
                  aria-haspopup="true" aria-expanded="false">Dropdown</button>
                <div class="dropdown-menu">
                  <a class="dropdown-item" href="#">Search Drink by Name</a>
                  <a class="dropdown-item" href="#">Search Ingrediant by Name</a>
                  <a class="dropdown-item" href="#">Search by Ingrediant</a>
                  <a class="dropdown-item" href="#">Random</a>
                </div>
              </div>
              <input type="text" class="form-control" aria-label="Text input with dropdown button" placeholder="Search"
                onChange={props.handleInputChange} value={props.search} />
              <button type="submit" className="btn btn-secondary btn-md btn-block" onClick={props.handleFormSubmit}>Search</button>
            </div>
            {/* </form> */}
          </div>
        </div>
      </div>
    </div>
  );
}
export default Search;
