import React from "react"
// import Drink from '../Drink';
import "./style.css"
// import Search from "../Search";
// import axios from 'axios';

function Results(props) {
  switch (props.searchBy) {
    case "searchByName" || "searchByIngrediant":
      return (props.drinks.length === 0) ? (
        <div>
          <h4 className="text-center">Search for a Drink</h4>
        </div>
      ) : (
          <div className="row">
            {props.drinks.map((drink, index) => {
              return (
                <div className="col-lg-4 col-md-6 col-sm-12 p-3" id={drink.id} key={index}>
                  <h4 className="text-center">{drink.title}</h4>
                  <div className="d-flex justify-content-center">
                    <img src={drink.img} alt={drink.title} />
                  </div>
                </div>
              )
            })}

          </div>
        );
      break;
    case "searchIngrediantByName":

      break;
    case "random":

      break;

    default:
      break;
  }
  console.log(props.drinks)

}
