import React, { Component } from "react";
import API from "../../utils/API";
import "./style.css"

class Drink extends Component {

  state = {
    // search: "",
    // searchBy: "",
    // drinkArray: [],
    searchedDrink: "",
    searchedInfo: [],
    error: ""
  }

  handleData = event => {
    this.setState({ searchedDrink: event.target.alt })
    console.log(this.state.searchedDrink)
    event.preventDefault();
    API.searchByName(this.state.searchedDrink)
      .then((res) => {
        console.log(res)
        if (res.data.drinks === "error") {
          throw new Error(res.data.drinks);
        } else {
          let results = res.data.drinks
          results = results.map(result => {
            //store each book information in a new object 
            result = {
              id: result.idDrink,
              title: result.strDrink,
              img: result.strDrinkThumb,
              instructions: result.strInstructions,
              ingredientsArr: [result.strIngredient1, result.strIngredient2, result.strIngredient3, result.strIngredient4,
              result.strIngredient5, result.strIngredient6, result.strIngredient7, result.strIngredient8, result.strIngredient9,
              result.strIngredient10],
              measureArr: [result.strMeasure1, result.strMeasure2, result.strMeasure3, result.strMeasure4,
              result.strMeasure5, result.strMeasure6, result.strMeasure7, result.strMeasure8, result.strMeasure9,
              result.strIngredient10]
            }
            return result;
          })
          this.setState({ searchedInfo: results, error: "" })

        }
      })
      .catch(err => this.setState({ error: err.items }));

    //if the state matches the event.arget.value no search is needed

    // else if state does not match event target hit the api
  }

  render() {
    return (
      <div className="col-lg-4 col-md-6 col-sm-12 p-3" id={this.props.drink.id}>
        <h4 className="text-center">{this.props.drink.title}</h4>
        <div className="d-flex justify-content-center">
          <img src={this.props.drink.img} alt={this.props.drink.title} onClick={this.handleData} data-toggle="modal" data-target={`.modal-${this.props.drink.id}`} />

          <div className={`modal fade modal-${this.props.drink.id}`} tabIndex="-1" role="dialog" aria-labelledby="myExtraLargeModalLabel" aria-hidden="true">
            <div className="modal-dialog modal-xl">
              <div className="modal-content">
                <div className="container p-4">
                  <div className="row">
                    <div className="col-md-12">
                      <h1 className="text-center">{this.props.drink.title}</h1>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-md-3 d-flex justify-content-center">
                      <img src={this.props.drink.img} alt={this.props.drink.title} />
                    </div>

                    <div className="col-md-3">
                      <h4>Ingredients</h4>
                      <div className="row">
                        <ul>
                          {this.props.drink.ingredientsArr.map((ingredient, index) => {
                            if (ingredient === "") {
                              return (<></>)
                            } else {
                              return (
                                <li key={index}> {ingredient}</li>
                              )
                            }

                          })}
                        </ul>
                      </div>

                    </div>
                    <div className="col-md-3">
                      <div className="row">
                        <h4>Measurements</h4>
                      </div>
                      <div className="row">
                        <ul id="measureList">
                          {this.props.drink.measureArr.map((measure, index) => {
                            if (measure === " ") {
                              return (<></>)
                            } else if (measure === "↵") {
                              return (<></>)
                            } else if (measure === "") {
                              return (<></>)
                            } else {
                              return (
                                <li key={index}>{measure}</li>
                              )
                            }

                          })}
                        </ul>
                      </div>

                    </div>

                    <div className="col-md-3 ">
                      <div className="row">
                        <h4>Instructions</h4>
                        <p>{this.props.drink.instructions}</p>
                      </div>
                    </div>

                  </div>
                </div>
              </div>
            </div>
          </div>

        </div>
      </div>
    )
  }
}

export default Drink
