import React from "react";
import "./style.css";
import Results from "../Results";
import axios from "axios";
import drinks from "../Drink"


function Search(props) {


  searchDatabase = (query = "margarita", qType = "s") => {
    axios.get(`https://www.thecocktaildb.com/api/json/v2/8673533/search.php?${qType}=${query}`)
      .then((res) => {
        console.log(res);
        this.setState({
          drinkArray: res.data.drinks
        })
      })

    render()
    return (
      <div className="backgroundRow p-5 m-5">
        <div className="container my-4">
          <div className="row">
            <div className="col-md-2">
            </div>
            <div className="col-md-8">
              <form>
                <input id="search" class="form-control" type="text" placeholder="Search"></input>
                <button type="submit" class="btn btn-secondary btn-md btn-block" onClick={() =>
                  Results.searchDatabase(document.getElementById("search").value)}>
                  Search</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default Search;
