import React from 'react';
import axios from 'axios';

class Drink extends React.Component {
    state = {
        drink: [],
    }
    // When the form is submitted, search the OMDB API for the value of `this.state.search`
    handleClickEvent = event => {
        event.preventDefault();
        this.getDrinks();
    };

    getDrinks = () => {
        axios.get("https://www.thecocktaildb.com/api/json/v2/8673533/filter.php?i=Gin")
            .then(function (data) {
                // console.log(data);
                this.setState({ drink: data });
                console.log(data);
            })
            .catch(function (err) {
                console.log(err);
            });
    }


    render() {
        return (
            <div>
                <button onClick={this.handleClickEvent()}>Pizza</button>
            </div>
        )
    }
}

export default Drink;
