import React from "react"
import "./style.css"


function Results(props) {
  switch (props.searchBy) {
    case "searchByName":
      return <>
        {props.drinks.map((drink, index) => {
          return (
            <div className="col-lg-4 col-md-6 col-sm-12 p-3" id={drink.id} key={index}>
              <h4 className="text-center">{drink.title}</h4>
              <div className="d-flex justify-content-center">
                <img src={drink.img} alt={drink.title} />
              </div>
            </div>
          )
        })}
      </>
    case "searchByIngredient":
      return <>
        {props.drinks.map((drink, index) => {
          return (
            <div className="col-lg-4 col-md-6 col-sm-12 p-3" id={drink.id} key={index}>
              <h4 className="text-center">{drink.title}</h4>
              <div className="d-flex justify-content-center">
                <img src={drink.img} alt={drink.title} />
              </div>
            </div>
          )
        })}
      </>

    case "searchIngredientByName":
      return <>
        {props.drinks.map((drink, index) => {
          return (
            <div className="col-lg-12 col-md-12 col-sm-12 p-3" id={drink.id} key={index}>
              <h4 className="text-center">{drink.title}</h4>
              <p>{drink.description}</p>
            </div>
          )
        })}
      </>

    case "random":
      return <>
        {/* <div className="col-lg-6 col-md-6 col-sm-6 p-3" id={props.drinks.id} key={props.drinks.id}>
          <h4 className="text-center">{props.drinks.title}</h4>
          <div className="d-flex justify-content-center">
            <img src={props.drinks.img} alt={props.drinks.title} />
          </div>
        </div>
        <div className="col-lg-6 col-md-6 col-sm-6 p-3">
          <h3>Ingredients</h3>
          <ul>
            <li>{props.drinks.ingredients1}</li>
          </ul>
        </div> */}
        {props.drinks.map((drink, index) => {
          return (
            <div className="row">
              <div className="col-lg-6 col-md-6 col-sm-6 p-3" id={drink.id} key={index}>
                <h4 className="text-center">{drink.title}</h4>
                <div className="d-flex justify-content-center">
                  <img src={drink.img} alt={drink.title} />
                </div>
              </div>
              <div className="col-lg-6 col-md-6 col-sm-6 p-3" key={drink.id}>
                <h3>Ingredients</h3>
                <ul>
                  <li>{drink.ingredients1}  {drink.measure1}</li>
                  <li>{drink.ingredients2}  {drink.measure2}</li>
                  <li>{drink.ingredients3}  {drink.measure3}</li>
                  <li>{drink.ingredients4}  {drink.measure4}</li>
                  <li>{drink.ingredients5}  {drink.measure5}</li>
                  <li>{drink.ingredients6}  {drink.measure6}</li>
                  <li>{drink.ingredients7}  {drink.measure7}</li>
                  <li>{drink.ingredients8}  {drink.measure8}</li>
                  <li>{drink.ingredients9}  {drink.measure9}</li>
                  <li>{drink.ingredients10}  {drink.measure10}</li>
                </ul>
              </div>
            </div>
          )
        })}

      </>
    default:
      return <div className="col-md-12 text-center">
        <h1>Search for some Great Drinks!</h1>
      </div>
  }
}
export default Results
