import React, { Component } from "react";
// import Nav from "./components/Nav/index";
import { Container, Row, Col } from "./components/Grid";
import Search from "./components/Search";
import Results from './components/Results';
// import axios from "axios"
import API from "./utils/API";
// import Drink from './components/Drink';

class App extends Component {

  state = {
    search: "",
    searchBy: "",
    drinkArray: [],
    error: ""
  }

  handleInputChange = event => {
    this.setState({ search: event.target.value })
  }

  handleSelectChange = event => {

  }


  handleFormSubmit = event => {
    event.preventDefault();
    API.searchByName(this.state.search)
      .then((res) => {
        console.log(res)
        if (res.data.drinks === "error") {
          throw new Error(res.data.drinks);
        } else {
          let results = res.data.drinks
          results = results.map(result => {
            //store each book information in a new object 
            result = {
              key: result.idDrink,
              id: result.idDrink,
              title: result.strDrink,
              img: result.strDrinkThumb
            }
            return result;
          })
          this.setState({ drinkArray: results, error: "" })

        }
      })
      .catch(err => this.setState({ error: err.items }));
    console.log(this.state.drinkArray)
  }

  render() {
    return (
      <div>
        <h1 className="text-center"><b>SpeakEasy</b></h1>
        <Search handleFormSubmit={this.handleFormSubmit} handleInputChange={this.handleInputChange} />
        <Container>
          <Results drinks={this.state.drinkArray} />
        </Container>

      </div >
    );
  }
}


export default App;
