import React from "react";
import "./style.css";
// import Results from "../Results";
// import axios from "axios";
// import drinks from "../Drink"


function Search(props) {
  return (
    <div className="backgroundRow p-5 m-5">
      <div className="container my-4">
        <div className="row">
          <div className="col-md-2">
          </div>
          <div className="col-md-8">
            <form>
              <input class="form-control" type="text" placeholder="Search" onChange={props.handleInputChange} value={props.search}></input>
              <button type="submit" class="btn btn-secondary btn-md btn-block" onClick={props.searchDatabase}>Search</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Search;
