import React, { Component } from "react";
// import Nav from "./components/Nav/index";
// import { Container, Row, Col } from "./components/Grid";
import Search from "./components/Search";
import Results from './components/Results';
// import axios from "axios"
import API from "./utils/API";
// import Drink from './components/Drink';

class App extends Component {
  state = {
    search: "",
    drinkArray: [],
    error: ""
  }

  handleInputChange = event => {
    this.setState({ search: event.target.value })
  }


  searchDatabase = event => {
    event.preventDefault();
    API.getdrinks(this.state.search)
      .then((res) => {
        if (res.data.itmes === "error") {
          throw new Error(res.data.items);
        } else {
          let results = res.data.items

          results = results.map(result => {
            //store each book information in a new object 
            result = {
              key: result.idDrink,
              id: result.idDrink,
              title: result.strDrink,
              img: result.strDrinkThumb
            }
            return result;
          })
          this.setState({ drinkArray: results, error: "" })

        }
      })
      .catch(err => this.setState({ error: err.items }));
    console.log(this.state.drinkArray)
  }

  render() {
    return (
      <div>
        <h1 className="text-center"><b>SpeakEasy</b></h1>
        <Search searchDatabase={this.searchDatabase} handleInputChange={this.handleInputChange} />

        <Results drinks={this.state.drinkArray} />

      </div >
    );
  }
}


export default App;
