import React, { Component } from "react";
import "./style.css"
// import { Container } from "../Grid";
import Drink from "../Drink";


class Results extends Component {


  render() {
    switch (this.props.searchBy) {
      case "searchByName":
        return <div className="row">
          {this.props.drinks.map((drink) => {
            return (
              <Drink drink={drink} key={drink.id} />
            )
          })}
        </div>
      case "searchByIngredient":
        return <div className="row">
          {this.props.drinks.map((drink) => {
            return (
              <Drink drink={drink} key={drink.id} />
            )
          })}
        </div>

      case "searchIngredientByName":
        return <div className="row">
          {this.props.drinks.map((drink) => {
            return (
              <div className="col-lg-12 col-md-12 col-sm-12 p-3" id={drink.id} key={drink.id}>
                <h4 className="text-center">{drink.title}</h4>
                <p>{drink.description}</p>
              </div>
            )
          })}
        </div>

      case "random":
        return <div className="row">
          {this.props.drinks.map((drink) => {
            return (
              <>
                <div className="row">
                  <div className="col-md-12">
                    <h1 className="text-center">{this.props.drink.title}</h1>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-4 d-flex justify-content-center">
                    <img src={this.props.drink.img} alt={this.props.drink.title} />
                  </div>

                  <div className="col-md-2">
                    <h4>Ingredients</h4>
                    <div className="row">
                      <ul>
                        {this.props.drink.ingredientsArr.map((ingredient, index) => {
                          if (ingredient === "") {
                            return (<></>)
                          } else {
                            return (
                              <li key={index}> {ingredient}</li>
                            )
                          }

                        })}
                      </ul>
                    </div>

                  </div>
                  <div className="col-md-2">
                    <div className="row">
                      <h4>Measurements</h4>
                    </div>
                    <div className="row">
                      <ul id="measureList">
                        {this.props.drink.measureArr.map((measure, index) => {
                          if (measure === " ") {
                            return (<></>)
                          } else if (measure === "â†µ") {
                            return (<></>)
                          } else if (measure === "") {
                            return (<></>)
                          } else {
                            return (
                              <li key={index}>{measure}</li>
                            )
                          }

                        })}
                      </ul>
                    </div>

                  </div>

                  <div className="col-md-4 d-flex justify-content-center">
                    <p>{this.props.drink.instructions}</p>
                  </div>

                </div>
              </>
            )
          })}

        </div>
      default:
        return <div className="col-md-12 text-center">
          <h1>Search for some Great Drinks!</h1>
        </div>
    }
  }
}
export default Results
